package UD04;

public class Ejercicio3 {

	public Ejercicio3() {
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Declaro e inicializo las variables
		int x = 1;
		int y = 2;
		double n = 5.5;
		double m = 9.18;
		
		//Muestro por pantalla el valor de las variables
		System.out.println("El valor de la variable x es = "+x);
		System.out.println("El valor de la variable y es = "+y);
		System.out.println("El valor de la variable n es = "+n);
		System.out.println("El valor de la variable m es = "+m);
		
		//Muestro por pantalla la suma de (x + y)
		System.out.println("La suma de x + y = "+(x+y));
		//Muestro por pantalla la diferencia de (x - y)
		System.out.println("La diferencia de x - y = "+(x-y));
		//Muestro por pantalla el producto de (x * y)
		System.out.println("El producto de x * y = "+(x*y));
		//Muestro por pantalla el cociente de (x / y)
		System.out.println("El cociente de x / y = "+(x/y));
		//Muestro por pantalla el residuo de (x / y)
		System.out.println("El resto de x / y = "+(x%y));
		//Muestro por pantalla la suma de (n + m)
		System.out.println("La suma de n + m = "+(n+m));
		//Muestro por pantalla la diferencia de (n - m)
		System.out.println("La suma de n - m = "+(n-m));
		//Muestro por pantalla el producto de (n * m)
		System.out.println("El producto de n * m = "+(n*m));
		//Muestro por pantalla el cociente de (n / m)
		System.out.println("El cociente de n / m = "+(n/m));
		//Muestro por pantalla el residuo de (n / m)
		System.out.println("El resto de n / m = "+(n%m));
		//Muestro por pantalla la suma de (x + n)
		System.out.println("La suma de x + n = "+(x+n));
		//Muestro por pantalla el cociente de (y / m)
		System.out.println("El cociente de y / m es = "+(y/m));
		//Muestro por pantalla el residuo de (n / m)
		System.out.println("El residuo de n / m = "+(n%m));
		//Muestro por pantalla la suma de (x + n)
		System.out.println("La suma de x + n = "+(x+n));
		//Muestro por pantalla el cociente de (y / n)
		System.out.println("El cociente de y / n = "+(y/n));
		//Muestro por pantalla el residuo de (y / n)
		System.out.println("El residuo de y / n = "+(y%n));
		
	}

}
